import React, { useState, useEffect } from 'react';
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';
import axios from 'axios';

const containerStyle = {
  width: '100%',
  height: '400px',
  borderRadius: '8px'
};

function Map({ markers = [], onMarkerClick }) {
  const [userLocation, setUserLocation] = useState(null);
  const [mapConfig, setMapConfig] = useState({
    center: { lat: 40.6304, lng: -74.0212 },
    zoom: 14
  });
  const [apiKeyLoaded, setApiKeyLoaded] = useState(false);
  const [apiKey, setApiKey] = useState('');

  // Fetch API key first
  useEffect(() => {
    axios.get('http://localhost:5000/api/location')
      .then(response => {
        setApiKey(response.data.data.apiKey);
        setApiKeyLoaded(true);
      })
      .catch(error => {
        console.error('Error fetching API key:', error);
      });
  }, []);

  // Only initialize the loader after we have the API key
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: apiKey,
    libraries: ['places']
  });

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.watchPosition(
        (position) => {
          const newLocation = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setUserLocation(newLocation);
          setMapConfig(prev => ({ ...prev, center: newLocation }));
        },
        (error) => {
          console.error('Error getting location:', error);
        },
        {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0
        }
      );
    }
  }, []);

  // Show loading state while waiting for API key and map to load
  if (!apiKeyLoaded || !isLoaded) return <div>Loading...</div>;

  const userMarkerIcon = {
    path: 2,
    scale: 8,
    fillColor: '#4285F4',
    fillOpacity: 1,
    strokeColor: '#ffffff',
    strokeWeight: 2,
  };

  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={mapConfig.center}
      zoom={mapConfig.zoom}
    >
      {userLocation && (
        <Marker
          position={userLocation}
          icon={userMarkerIcon}
        />
      )}
      {markers.map((marker, index) => (
        <Marker
          key={index}
          position={marker.position}
          onClick={() => onMarkerClick(marker)}
        />
      ))}
    </GoogleMap>
  );
}

export default React.memo(Map);
