import React from 'react';
import { Box, Card, CardContent, CardMedia, Typography, IconButton } from '@mui/material';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import ThumbDownIcon from '@mui/icons-material/ThumbDown';
import PersonIcon from '@mui/icons-material/Person';

const ContentView = () => {
  // Mock data - this would come from your backend later
  const contentItems = [
    {
      id: 1,
      type: 'video',
      title: 'Best Places in Brooklyn',
      creator: 'Travel Guide',
      thumbnail: 'https://via.placeholder.com/300x200',
      upvotes: 124,
      downvotes: 5
    },
    {
      id: 2,
      type: 'blog',
      title: 'Hidden Gems of NYC',
      creator: 'Local Explorer',
      content: 'Discover the secret spots that tourists never see...',
      upvotes: 89,
      downvotes: 3
    },
    {
      id: 3,
      type: 'image',
      title: 'Sunset at Brooklyn Bridge',
      creator: 'NYC Photography',
      imageUrl: 'https://via.placeholder.com/300x200',
      upvotes: 256,
      downvotes: 12
    }
  ];

  const handleUpvote = (id) => {
    console.log('Upvoted:', id);
  };

  const handleDownvote = (id) => {
    console.log('Downvoted:', id);
  };

  return (
    <Box sx={{ pb: 7, pt: 2 }}>
      {contentItems.map((item) => (
        <Card key={item.id} sx={{ mb: 2, borderRadius: 2 }}>
          {(item.type === 'video' || item.type === 'image') && (
            <CardMedia
              component="img"
              height="200"
              image={item.type === 'video' ? item.thumbnail : item.imageUrl}
              alt={item.title}
            />
          )}
          <CardContent>
            <Typography variant="h6" gutterBottom>
              {item.title}
            </Typography>
            {item.type === 'blog' && (
              <Typography variant="body2" color="text.secondary" paragraph>
                {item.content}
              </Typography>
            )}
            <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <PersonIcon color="action" />
                <Typography variant="body2" color="text.secondary">
                  {item.creator}
                </Typography>
              </Box>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <IconButton onClick={() => handleUpvote(item.id)} size="small">
                  <ThumbUpIcon />
                </IconButton>
                <Typography variant="body2">{item.upvotes}</Typography>
                <IconButton onClick={() => handleDownvote(item.id)} size="small">
                  <ThumbDownIcon />
                </IconButton>
                <Typography variant="body2">{item.downvotes}</Typography>
              </Box>
            </Box>
          </CardContent>
        </Card>
      ))}
    </Box>
  );
};

export default ContentView;
