import React, { useState, useEffect, useRef } from 'react';
import { Box, Card, CardContent, CardMedia, Typography, IconButton } from '@mui/material';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import ThumbDownIcon from '@mui/icons-material/ThumbDown';
import PersonIcon from '@mui/icons-material/Person';
import axios from 'axios';

const ContentView = () => {
  const [contentItems, setContentItems] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const containerRef = useRef(null);
  const [touchStart, setTouchStart] = useState(null);
  const [scrolling, setScrolling] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const videoRef = useRef(null);
  const iframeRef = useRef(null);
  const cardRef = useRef(null);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/content');
        if (response.data.status === 'success') {
          setContentItems(response.data.data);
          setLoading(false);
        }
      } catch (err) {
        setError('Failed to fetch content');
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  const getYouTubeVideoId = (url) => {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11) ? match[2] : null;
  };

  const handleScroll = (event) => {
    event.preventDefault();
    if (scrolling || contentItems.length === 0) return;
  
    const deltaY = event.deltaY;
    if (deltaY > 0) {
      setScrolling(true);
      setCurrentIndex(prev => (prev + 1) % contentItems.length);
    } else if (deltaY < 0) {
      setScrolling(true);
      setCurrentIndex(prev => (prev - 1 + contentItems.length) % contentItems.length);
    }
  };

  const handleTouchStart = (e) => {
    setTouchStart(e.touches[0].clientY);
  };

  const handleTouchMove = (e) => {
    if (!touchStart) return;

    const touchEnd = e.touches[0].clientY;
    const deltaY = touchStart - touchEnd;

    if (Math.abs(deltaY) > 50) {
      if (deltaY > 0 && currentIndex < contentItems.length - 1) {
        setCurrentIndex(prev => prev + 1);
      } else if (deltaY < 0 && currentIndex > 0) {
        setCurrentIndex(prev => prev - 1);
      }
      setTouchStart(null);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => setScrolling(false), 1000);
    return () => clearTimeout(timer);
  }, [currentIndex]);

  // Handle scroll events for the entire component
  useEffect(() => {
    const handleWheelEvent = (e) => {
      e.preventDefault();
      handleScroll(e);
    };

    const container = containerRef.current;
    const card = cardRef.current;
    const video = videoRef.current;
    const iframe = iframeRef.current;

    if (container) {
      container.addEventListener('wheel', handleWheelEvent, { passive: false });
    }
    if (card) {
      card.addEventListener('wheel', handleWheelEvent, { passive: false });
    }
    if (video) {
      video.addEventListener('wheel', handleWheelEvent, { passive: false });
    }
    if (iframe) {
      iframe.addEventListener('wheel', handleWheelEvent, { passive: false });
    }

    return () => {
      if (container) {
        container.removeEventListener('wheel', handleWheelEvent);
      }
      if (card) {
        card.removeEventListener('wheel', handleWheelEvent);
      }
      if (video) {
        video.removeEventListener('wheel', handleWheelEvent);
      }
      if (iframe) {
        iframe.removeEventListener('wheel', handleWheelEvent);
      }
    };
  }, [scrolling, currentIndex, contentItems.length]);

  const renderVideoContent = (videoUrl) => {
    const videoId = getYouTubeVideoId(videoUrl);
    
    if (videoId) {
      return (
        <Box 
          sx={{ 
            flex: 1, 
            position: 'relative', 
            width: '100%', 
            height: '100%',
            overflow: 'hidden'
          }}
          onWheel={handleScroll}
        >
          <iframe
            ref={iframeRef}
            width="100%"
            height="100%"
            src={`https://www.youtube.com/embed/${videoId}?autoplay=1&mute=1&enablejsapi=1`}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
            style={{ 
              position: 'absolute', 
              top: 0, 
              left: 0,
              pointerEvents: 'none' // This prevents iframe from capturing mouse events
            }}
          />
        </Box>
      );
    } else {
      return (
        <Box 
          sx={{ 
            flex: 1, 
            position: 'relative',
            overflow: 'hidden'
          }}
          onWheel={handleScroll}
        >
          <video
            ref={videoRef}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              pointerEvents: 'none' // This prevents video from capturing mouse events
            }}
            src={videoUrl}
            controls
            autoPlay
            muted
            playsInline
          />
        </Box>
      );
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="calc(100vh - 56px)">
        <Typography>Loading content...</Typography>
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="calc(100vh - 56px)">
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  const currentItem = contentItems[currentIndex];

  return (
    <Box
      ref={containerRef}
      sx={{
        height: 'calc(100vh - 56px)',
        overflow: 'hidden',
        position: 'relative',
        bgcolor: '#f5f5f5'
      }}
      onWheel={handleScroll}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
    >
      <Box
        sx={{
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          position: 'relative'
        }}
      >
        <Box
          sx={{
            flex: 1,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            p: 2
          }}
        >
          {currentItem && (
            <Card
              ref={cardRef}
              sx={{
                width: '100%',
                maxWidth: '500px',
                height: '80%',
                display: 'flex',
                flexDirection: 'column',
                overflow: 'hidden',
                position: 'relative'
              }}
            >
              {currentItem.type === 'video' ? (
                renderVideoContent(currentItem.video_url)
              ) : currentItem.type === 'image' ? (
                <CardMedia
                  component="img"
                  sx={{
                    flex: 1,
                    objectFit: 'cover'
                  }}
                  image={currentItem.image_url}
                  alt={currentItem.title}
                />
              ) : (
                <CardContent sx={{ flex: 1, overflow: 'auto' }}>
                  <Typography variant="body1">{currentItem.content}</Typography>
                </CardContent>
              )}
            </Card>
          )}
        </Box>

        <Box
          sx={{
            position: 'absolute',
            right: 16,
            top: '50%',
            transform: 'translateY(-50%)',
            display: 'flex',
            flexDirection: 'column',
            gap: 2,
            zIndex: 1
          }}
        >
          <IconButton
            sx={{ bgcolor: 'white', '&:hover': { bgcolor: 'grey.100' } }}
            onClick={() => {/* Implement upvote place */}}
          >
            <ThumbUpIcon />
          </IconButton>
          <IconButton
            sx={{ bgcolor: 'white', '&:hover': { bgcolor: 'grey.100' } }}
            onClick={() => {/* Implement downvote place */}}
          >
            <ThumbDownIcon />
          </IconButton>
          <IconButton
            sx={{ bgcolor: 'white', '&:hover': { bgcolor: 'grey.100' } }}
            onClick={() => {/* Implement upvote creator */}}
          >
            <PersonIcon />
            <ThumbUpIcon sx={{ fontSize: '0.8em', ml: -0.5 }} />
          </IconButton>
          <IconButton
            sx={{ bgcolor: 'white', '&:hover': { bgcolor: 'grey.100' } }}
            onClick={() => {/* Implement downvote creator */}}
          >
            <PersonIcon />
            <ThumbDownIcon sx={{ fontSize: '0.8em', ml: -0.5 }} />
          </IconButton>
        </Box>
      </Box>
    </Box>
  );
};

export default ContentView;
