import React, { useState, useEffect, useRef } from 'react';
import { Box, Card, CardContent, CardMedia, Typography, IconButton } from '@mui/material';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import ThumbDownIcon from '@mui/icons-material/ThumbDown';
import PersonIcon from '@mui/icons-material/Person';
import axios from 'axios';

const ContentView = () => {
  const [contentItems, setContentItems] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const containerRef = useRef(null);
  const [touchStart, setTouchStart] = useState(null);
  const [scrolling, setScrolling] = useState(false);

  useEffect(() => {
    fetchContent();
  }, []);

  const fetchContent = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/content');
      if (response.data.status === 'success') {
        setContentItems(response.data.data);
        setLoading(false);
      }
    } catch (err) {
      setError('Failed to fetch content');
      setLoading(false);
    }
  };

  const handleVote = async (contentId, voteType) => {
    try {
      const response = await axios.post(`http://localhost:5000/api/content/${contentId}/vote`, {
        type: voteType
      });
      if (response.data.status === 'success') {
        const updatedContent = response.data.data;
        setContentItems(items => 
          items.map(item => 
            item.id === contentId ? updatedContent : item
          )
        );
      }
    } catch (err) {
      console.error('Failed to vote:', err);
    }
  };

  const handleScroll = (event) => {
    if (scrolling || !contentItems.length) return;

    const deltaY = event.deltaY;
    if (deltaY < 0 && currentIndex < contentItems.length - 1) {
      setScrolling(true);
      setCurrentIndex(prev => prev + 1);
    } else if (deltaY > 0 && currentIndex > 0) {
      setScrolling(true);
      setCurrentIndex(prev => prev - 1);
    }
  };

  const handleTouchStart = (e) => {
    setTouchStart(e.touches[0].clientY);
  };

  const handleTouchMove = (e) => {
    if (!touchStart || !contentItems.length) return;

    const touchEnd = e.touches[0].clientY;
    const deltaY = touchStart - touchEnd;

    if (Math.abs(deltaY) > 50) {
      if (deltaY > 0 && currentIndex < contentItems.length - 1) {
        setCurrentIndex(prev => prev + 1);
      } else if (deltaY < 0 && currentIndex > 0) {
        setCurrentIndex(prev => prev - 1);
      }
      setTouchStart(null);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => setScrolling(false), 1000);
    return () => clearTimeout(timer);
  }, [currentIndex]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography>Loading...</Typography>
      </Box>
    );
  }

  if (error || !contentItems.length) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography color="error">{error || 'No content available'}</Typography>
      </Box>
    );
  }

  const currentItem = contentItems[currentIndex];

  return (
    <Box
      ref={containerRef}
      sx={{
        height: 'calc(100vh - 160px)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        overflow: 'hidden',
      }}
      onWheel={handleScroll}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
    >
      <Card 
        sx={{ 
          width: '90%',
          maxWidth: '400px',
          borderRadius: 2,
          boxShadow: '0px 2px 8px rgba(0,0,0,0.1)',
          bgcolor: 'background.paper',
          overflow: 'hidden'
        }}
      >
        {(currentItem.type === 'video' || currentItem.type === 'image') && (
          <CardMedia
            component="img"
            sx={{
              width: '100%',
              height: 'auto',
              maxHeight: '250px',
              objectFit: 'cover'
            }}
            image={currentItem.type === 'video' ? currentItem.thumbnail : currentItem.image_url}
            alt={currentItem.title}
          />
        )}
        <CardContent sx={{ p: 2, pb: '16px !important' }}>
          <Box sx={{ mb: 1 }}>
            <Typography variant="h6" sx={{ fontSize: '1rem', fontWeight: 500 }}>
              {currentItem.title}
            </Typography>
          </Box>
          {currentItem.type === 'blog' && (
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2, whiteSpace: 'pre-line' }}>
              {currentItem.content}
            </Typography>
          )}
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between',
            mt: 1
          }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
              <PersonIcon sx={{ fontSize: '1.2rem' }} color="action" />
              <Typography variant="body2" color="text.secondary">
                {currentItem.creator}
              </Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
              <IconButton 
                size="small" 
                sx={{ p: 0.5 }}
                onClick={() => handleVote(currentItem.id, 'upvote')}
              >
                <ThumbUpIcon sx={{ fontSize: '1.2rem' }} />
              </IconButton>
              <Typography variant="body2">{currentItem.upvotes}</Typography>
              <IconButton 
                size="small" 
                sx={{ p: 0.5 }}
                onClick={() => handleVote(currentItem.id, 'downvote')}
              >
                <ThumbDownIcon sx={{ fontSize: '1.2rem' }} />
              </IconButton>
              <Typography variant="body2">{currentItem.downvotes}</Typography>
            </Box>
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
};

export default ContentView;
