import React, { useState, useEffect, useRef } from 'react';
import { Box, Card, CardContent, CardMedia, Typography, IconButton } from '@mui/material';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import ThumbDownIcon from '@mui/icons-material/ThumbDown';
import PersonIcon from '@mui/icons-material/Person';
import axios from 'axios';

const ContentView = () => {
  // Existing state declarations remain the same
  const [contentItems, setContentItems] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const containerRef = useRef(null);
  const [touchStart, setTouchStart] = useState(null);
  const [scrolling, setScrolling] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Existing useEffect and fetch logic remains the same
  useEffect(() => {
    const fetchContent = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/content');
        if (response.data.status === 'success') {
          setContentItems(response.data.data);
          setLoading(false);
        }
      } catch (err) {
        setError('Failed to fetch content');
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  // Existing scroll handlers remain the same
  const handleScroll = (event) => {
    if (scrolling) return;

    const deltaY = event.deltaY;
    if (deltaY < 0 && currentIndex < contentItems.length - 1) {
      setCurrentIndex(prev => prev + 1);
    } else if (deltaY > 0 && currentIndex > 0) {
      setScrolling(true);
      setCurrentIndex(prev => prev - 1);
    }
  };

  const handleTouchStart = (e) => {
    setTouchStart(e.touches[0].clientY);
  };

  const handleTouchMove = (e) => {
    if (!touchStart) return;

    const touchEnd = e.touches[0].clientY;
    const deltaY = touchStart - touchEnd;

    if (Math.abs(deltaY) > 50) {
      if (deltaY > 0 && currentIndex < contentItems.length - 1) {
        setCurrentIndex(prev => prev + 1);
      } else if (deltaY < 0 && currentIndex > 0) {
        setCurrentIndex(prev => prev - 1);
      }
      setTouchStart(null);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => setScrolling(false), 1000);
    return () => clearTimeout(timer);
  }, [currentIndex]);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="calc(100vh - 56px)">
        <Typography>Loading content...</Typography>
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="calc(100vh - 56px)">
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  const currentItem = contentItems[currentIndex];

  return (
    <Box
      ref={containerRef}
      sx={{
        height: 'calc(100vh - 56px)',
        overflow: 'hidden',
        position: 'relative',
        bgcolor: '#f5f5f5'
      }}
      onWheel={handleScroll}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
    >
      {/* Main Content Area */}
      <Box
        sx={{
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          position: 'relative'
        }}
      >
        {/* Content Display */}
        <Box
          sx={{
            flex: 1,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            p: 2
          }}
        >
          {currentItem && (
            <Card
              sx={{
                width: '100%',
                maxWidth: '500px',
                height: '80%',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              {(currentItem.type === 'video' || currentItem.type === 'image') && (
                <CardMedia
                  component="img"
                  sx={{
                    flex: 1,
                    objectFit: 'cover'
                  }}
                  image={currentItem.type === 'video' ? currentItem.thumbnail : currentItem.image_url}
                  alt={currentItem.title}
                />
              )}
              {currentItem.type === 'blog' && (
                <CardContent sx={{ flex: 1, overflow: 'auto' }}>
                  <Typography variant="body1">{currentItem.content}</Typography>
                </CardContent>
              )}
            </Card>
          )}
        </Box>

        {/* Interaction Buttons */}
        <Box
          sx={{
            position: 'absolute',
            right: 16,
            top: '50%',
            transform: 'translateY(-50%)',
            display: 'flex',
            flexDirection: 'column',
            gap: 2
          }}
        >
          <IconButton
            sx={{ bgcolor: 'white', '&:hover': { bgcolor: 'grey.100' } }}
            onClick={() => {/* Implement upvote place */}}
          >
            <ThumbUpIcon />
          </IconButton>
          <IconButton
            sx={{ bgcolor: 'white', '&:hover': { bgcolor: 'grey.100' } }}
            onClick={() => {/* Implement downvote place */}}
          >
            <ThumbDownIcon />
          </IconButton>
          <IconButton
            sx={{ bgcolor: 'white', '&:hover': { bgcolor: 'grey.100' } }}
            onClick={() => {/* Implement upvote creator */}}
          >
            <PersonIcon />
            <ThumbUpIcon sx={{ fontSize: '0.8em', ml: -0.5 }} />
          </IconButton>
          <IconButton
            sx={{ bgcolor: 'white', '&:hover': { bgcolor: 'grey.100' } }}
            onClick={() => {/* Implement downvote creator */}}
          >
            <PersonIcon />
            <ThumbDownIcon sx={{ fontSize: '0.8em', ml: -0.5 }} />
          </IconButton>
        </Box>
      </Box>
    </Box>
  );
};

export default ContentView;
