import React, { useState, useEffect } from 'react';
import { Box, Container, CssBaseline, Typography, CircularProgress } from '@mui/material';
import Map from './components/Map';
import SearchBar from './components/SearchBar';
import CategoryTabs from './components/CategoryTabs';
import Navigation from './components/Navigation';
import axios from 'axios';
import './App.css';
import ContentView from './components/ContentView';

function App() {
  const [locations, setLocations] = useState([]);
  const [filteredLocations, setFilteredLocations] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('restaurants');
  const [navValue, setNavValue] = useState(1); // Default to Map view
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch locations from backend
  useEffect(() => {
    axios.get('/api/locations')
      .then(response => {
        setLocations(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to load locations');
        setLoading(false);
      });
  }, []);

  // Filter locations when category changes
  useEffect(() => {
    if (selectedCategory === 'all') {
      setFilteredLocations(locations);
    } else {
      setFilteredLocations(
        locations.filter(location => location.category === selectedCategory)
      );
    }
  }, [selectedCategory, locations]);

  const handleSearch = (searchTerm) => {
    if (!searchTerm.trim()) {
      setFilteredLocations(
        selectedCategory === 'all' 
          ? locations 
          : locations.filter(location => location.category === selectedCategory)
      );
      return;
    }

    const filtered = locations.filter(location => {
      const matchesSearch = location.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           location.address.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = selectedCategory === 'all' || location.category === selectedCategory;

      return matchesSearch && matchesCategory;
    });

    setFilteredLocations(filtered);
  };

  const handleMarkerClick = (marker) => {
    console.log('Marker clicked:', marker);
  };

  const handleLocationSelect = (location) => {
    console.log('Location selected:', location);
  };

  const renderContent = () => {
    if (loading) {
      return (
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
          <CircularProgress />
        </Box>
      );
    }

    if (error) {
      return (
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
          <Typography color="error">{error}</Typography>
        </Box>
      );
    }

    switch (navValue) {
      case 0: // Content view
        return <ContentView />;
      case 1: // Map view
        return (
          <>
            <SearchBar onSearch={handleSearch} />
            <CategoryTabs 
              selectedCategory={selectedCategory} 
              onCategoryChange={setSelectedCategory} 
            />
            <Map markers={filteredLocations} onMarkerClick={handleMarkerClick} />
          </>
        );
      case 2: // Itinerary view
        return (
          <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
            <Typography>Itinerary View (Coming Soon)</Typography>
          </Box>
        );
      default:
        return <Map markers={filteredLocations} onMarkerClick={handleMarkerClick} />;
    }
  };

  return (
    <Box sx={{ pb: 7 }}>
      <CssBaseline />
      <Container maxWidth="sm">
        {renderContent()}
      </Container>
      <Navigation value={navValue} onChange={setNavValue} />
    </Box>
  );
}

export default App;
