import React, { useState, useEffect, useRef } from 'react';
import { Box, Card, CardContent, CardMedia, Typography, IconButton } from '@mui/material';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import ThumbDownIcon from '@mui/icons-material/ThumbDown';
import PersonIcon from '@mui/icons-material/Person';

const ContentView = () => {
  // Mock data - this would come from your backend later
  const contentItems = [
    {
      id: 1,
      type: 'video',
      title: 'Best Places in Brooklyn',
      creator: 'Travel Guide',
      thumbnail: 'https://via.placeholder.com/300x200',
      upvotes: 124,
      downvotes: 5
    },
    {
      id: 2,
      type: 'blog',
      title: 'Hidden Gems of NYC',
      creator: 'Local Explorer',
      content: 'Discover the secret spots that tourists never see...',
      upvotes: 89,
      downvotes: 3
    },
    {
      id: 3,
      type: 'image',
      title: 'Sunset at Brooklyn Bridge',
      creator: 'NYC Photography',
      imageUrl: 'https://via.placeholder.com/300x200',
      upvotes: 256,
      downvotes: 12
    }
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const containerRef = useRef(null);
  const [touchStart, setTouchStart] = useState(null);
  const [scrolling, setScrolling] = useState(false);

  const handleScroll = (event) => {
    if (scrolling) return;

    const deltaY = event.deltaY;
    if (deltaY < 0 && currentIndex < contentItems.length - 1) {
      // Scrolling up - show next content
      setScrolling(true);
      setCurrentIndex(prev => prev + 1);
    } else if (deltaY > 0 && currentIndex > 0) {
      // Scrolling down - show previous content
      setScrolling(true);
      setCurrentIndex(prev => prev - 1);
    }
  };

  // Touch events for mobile
  const handleTouchStart = (e) => {
    setTouchStart(e.touches[0].clientY);
  };

  const handleTouchMove = (e) => {
    if (!touchStart) return;

    const touchEnd = e.touches[0].clientY;
    const deltaY = touchStart - touchEnd;

    if (Math.abs(deltaY) > 50) { // Minimum scroll distance
      if (deltaY > 0 && currentIndex < contentItems.length - 1) {
        // Swiping up - show next content
        setCurrentIndex(prev => prev + 1);
      } else if (deltaY < 0 && currentIndex > 0) {
        // Swiping down - show previous content
        setCurrentIndex(prev => prev - 1);
      }
      setTouchStart(null);
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => setScrolling(false), 1000);
    return () => clearTimeout(timer);
  }, [currentIndex]);

  const currentItem = contentItems[currentIndex];

  return (
    <Box
      ref={containerRef}
      sx={{
        height: 'calc(100vh - 160px)', // Height minus header and bottom nav
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        overflow: 'hidden',
      }}
      onWheel={handleScroll}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
    >
      <Card 
        sx={{ 
          width: '90%',
          maxWidth: '400px', // Limit maximum width
          borderRadius: 2,
          boxShadow: '0px 2px 8px rgba(0,0,0,0.1)',
          bgcolor: 'background.paper',
          overflow: 'hidden'
        }}
      >
        {(currentItem.type === 'video' || currentItem.type === 'image') && (
          <CardMedia
            component="img"
            sx={{
              width: '100%',
              height: 'auto',
              maxHeight: '250px', // Fixed height for media
              objectFit: 'cover'
            }}
            image={currentItem.type === 'video' ? currentItem.thumbnail : currentItem.imageUrl}
            alt={currentItem.title}
          />
        )}
        <CardContent sx={{ p: 2, pb: '16px !important' }}>
          <Box sx={{ mb: 1 }}>
            <Typography variant="h6" sx={{ fontSize: '1rem', fontWeight: 500 }}>
              {currentItem.title}
            </Typography>
          </Box>
          {currentItem.type === 'blog' && (
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              {currentItem.content}
            </Typography>
          )}
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between',
            mt: 1
          }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
              <PersonIcon sx={{ fontSize: '1.2rem' }} color="action" />
              <Typography variant="body2" color="text.secondary">
                {currentItem.creator}
              </Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
              <IconButton size="small" sx={{ p: 0.5 }}>
                <ThumbUpIcon sx={{ fontSize: '1.2rem' }} />
              </IconButton>
              <Typography variant="body2">{currentItem.upvotes}</Typography>
              <IconButton size="small" sx={{ p: 0.5 }}>
                <ThumbDownIcon sx={{ fontSize: '1.2rem' }} />
              </IconButton>
              <Typography variant="body2">{currentItem.downvotes}</Typography>
            </Box>
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
};

export default ContentView;
